import React, { Component } from "react";
import AllPost from './AllPost'
import Header from './Header'


export default class MainView extends Component {
    state = {
        posts : [],
        photos: [],
        coments:[]
    }

    componentDidMount(){
		fetch('http://localhost:3000/posts')
		.then(resp => resp.json())
		.then(data => {
			this.setState({
				posts: data
			})
    })


     fetch('http://localhost:3000/photos')
		.then(resp => resp.json())
		.then(data => {
			this.setState({
				photos: data
			})
    })

    fetch('http://localhost:3000/Comments')
		.then(resp => resp.json())
		.then(data => {
			this.setState({
				comments: data
			})
    })




    }



    render() {
		return(
            <div >
                 <Header />
                 <AllPost comments={this.state.coments} posts = {this.state.posts} photos={this.state.photos} />
            </div>

        )
	}
}
