import React, { Component } from "react";
export default class MyPost extends Component {
	state = {
		mypost: [],
		myComments: [],
	};
	componentDidMount() {
		fetch("http://localhost:3000/posts")
			.then((resp) => resp.json())
			.then((data) => {
				let newData = data.filter(
					(post) => post.user_id === this.props.currentUser
				);
				this.setState({
					mypost: newData,
				});
			});
